///src/components/ProjectMap.js


import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import Sidebar from './Sidebar';
import Column from './Column';
import blankCards from '../util/BlankCards';


const ProjectMap = () => {
    const [showModal, setShowModal] = useState(false);
    const [currentCard, setCurrentCard] = useState(null);
    const [cardsInMap, setCardsInMap] = useState([]);

    const onAddCard = (templateCard) => {
        // Assuming templateCard contains the 'stage' and 'title' properties from the clicked card
        setCurrentCard({ ...templateCard, id: `new-${Date.now()}` }); // Generate a temporary unique ID
        setShowModal(true);
      };


    const saveNewCard = (newCardDetails) => {
      setCardsInMap([...cardsInMap, { ...currentCard, ...newCardDetails, id: `card-${cardsInMap.length + 1}` }]);
      setShowModal(false);
    };

    const saveEditedCard = (newCardDetails) => {
        setCardsInMap([...cardsInMap, { ...currentCard, ...newCardDetails, id: `card-${cardsInMap.length + 1}` }]);
        setShowModal(false);
      };

    const onEditCard = (cardId) => {
        const cardToEdit = cardsInMap.find(card => card.id === cardId);
        if (cardToEdit) {
          setCurrentCard(cardToEdit);
          setShowModal(true);
        }
      };


  const stages = ['empathy', 'define', 'ideate', 'prototype', 'test'];
  const cardsByStage = (stage) => blankCards.filter(card => card.stage === stage);
  const columnClasses = {
    empathy: "text-primary bg-light",
    define: "text-success bg-light",
    ideate: "text-info bg-light",
    prototype: "text-warning bg-light",
    test: "text-danger bg-light"
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <Sidebar onAddCard={onAddCard} />
        <div className="col-md-10 d-flex flex-grow-1 well"> {/* Make sure this is flex and can grow */}
          {stages.map(stage => (
            <Column key={stage} title={stage} stage = {stage} cards={cardsInMap.filter(card => card.stage === stage)} onEditCard = {onEditCard}/>
          ))}
        </div>
      </div>
<Modal show={showModal} onHide={() => setShowModal(false)}>
  <Modal.Header closeButton className={currentCard ? stageClasses[currentCard.stage] : ''}>
    <Modal.Title>
      {currentCard ? `Edit ${currentCard.title} Card` : 'Create Card'}
    </Modal.Title>
  </Modal.Header>
  <Modal.Body>
    <Form>
      <Form.Group className="mb-3" controlId="formCardTitle">
        <Form.Label>Title</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter title"
          defaultValue={currentCard?.title}
          onChange={(e) => setCurrentCard({ ...currentCard, title: e.target.value })}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formCardDetails">
        <Form.Label>Details</Form.Label>
        <Form.Control
          as="textarea"
          rows={3}
          placeholder="Enter details"
          defaultValue={currentCard?.details}
          onChange={(e) => setCurrentCard({ ...currentCard, details: e.target.value })}
        />
      </Form.Group>
      </Form>
  </Modal.Body>
  <Modal.Footer>
    <Button variant="secondary" onClick={() => setShowModal(false)}>
      Close
    </Button>
    <Button variant="primary" onClick={currentCard ? () => saveEditedCard() : () => saveNewCard()}>
      Save Changes
    </Button>
  </Modal.Footer>
</Modal>




    </div>


  );
};

export default ProjectMap;

