///src/components/Column.js

import React from 'react';
import Card from './Card';

const Column = ({ title, cards, stage }) => {
    const stageClasses = {
        empathy: "btn-primary",
        define: "btn-success",
        ideate: "btn-info",
        prototype: "btn-warning",
        test: "btn-danger"
      };
  return (
    <div className="flex-grow-1 min-width-0  ${stageClasses[stage]}"> {/* Add flex-grow and min-width */}
      <h2>{title}</h2>
      <div className="d-flex flex-column">
        {cards.map(card => (
          <Card key={card.id} {...card} />
        ))}
      </div>
    </div>
  );
};

export default Column;
