///src/components/Sidebar.js

import { Button } from 'react-bootstrap';
import blankCards from '../util/BlankCards';

const Sidebar = ({ onAddCard }) => {
  const buttonClasses = {
    empathy: "btn-primary",
    define: "btn-success",
    ideate: "btn-info",
    prototype: "btn-warning",
    test: "btn-danger"
  };

  return (
    <div className="col-md-2">
      {blankCards.map((card) => (
        <Button key={card.id} variant="secondary" className="w-100 mb-2" onClick={() => onAddCard(card)}>
          {card.title}
        </Button>
      ))}
    </div>
  );
};

export default Sidebar;

